require 'spec_helper'
require 'advent2'

describe 'advent of code - puzzle 2' do
  it 'moves a position' do
    number_pad = NumberPad.new

    position = number_pad.press_button('5', 'R')

    expect(position).to eq('6')
  end

  it 'processes list' do
    number_pad = NumberPad.new

    current_position = number_pad.press_buttons('5', 'ULL')

    expect(current_position).to eq('5')
  end

  it 'processes list' do
    code_cracker = CodeCracker.new

    code = code_cracker.crack("ULL\nRRDDD")

    expect(code).to eq('5D')
  end

  it 'processes loads of input' do
    code_cracker = CodeCracker.new
    input = """



"""


    code = code_cracker.crack(input)

    expect(code).to eq('99C3D')
  end
end
